import { Component } from '@angular/core';

@Component({
  selector: 'app-chatbot',
  templateUrl: './chatbot.component.html',
  styleUrls: ['./chatbot.component.css']
})
export class ChatbotComponent {

}
<div id="chat-container" style="position: fixed; bottom: 20px; right: 20px; z-index: 9999;">
  
  <!-- Chat Icon -->
  <div id="chat-toggle"
       onclick="
         var box = document.getElementById('chat-box');
         box.style.display = (box.style.display === 'block') ? 'none' : 'block';
       "
       onmouseover="this.style.boxShadow='0 4px 12px rgba(0,0,0,0.3)'"
       onmouseout="this.style.boxShadow='0 2px 6px rgba(0,0,0,0.2)'"
       style="width: 70px; height: 70px; border-radius: 35px;display: flex; align-items: center; justify-content: center;
              cursor: pointer; box-shadow: 0 2px 6px rgba(0,0,0,0.2); transition: box-shadow 0.3s ease;">
    <img src="https://gapcloud.com.au/wp-content/uploads/2021/10/Transparent-Logo-150x150.png"
         alt="Chat Icon"
         style="width: 100%; height: 100%; object-fit: contain;" />
  </div>

  <!-- Chat Box (Initially Hidden) -->
  <div id="chat-box"
       style="display: none; position: absolute; bottom: 80px; right: 0; width: 350px; height: 500px;
              border: 1px solid #ccc; box-shadow: 0 0 10px rgba(0,0,0,0.3); border-radius: 10px; overflow: hidden;">
    <iframe src="https://portal.vokals.ai/chat/default"
            style="width: 100%; height: 100%; border: none;"></iframe>
  </div>

</div>
